import React from'react'
import epilovers from './epilovers.png'

class Top extends React.Component   {
    constructor()   {
        super()
        this.state=({
            changed: true,
            responsif: "menuResponsif fermerMenuResponsif"
        })
    }
    changeVariable()    {
        if (this.state.changed ===false) {
            this.setState({
                responsif : "menuResponsif fermerMenuResponsif"
            })
        }
        if (this.state.changed===true) {
            this.setState({
                responsif: "menuResponsif ouvertMenuResponsif"
            })
        }
        
        this.setState ({
            changed: !this.state.changed,
        });
        console.log(this.state.changed)
    }
    render(){
        return(
            <div>
                <div className ="toolsBar">
                    <div className ="topContainer">
                        <img src ={epilovers} width ="10%" float="right"></img>
                        <div className="mainMenu">
                            <a href ="#">ACTUALITES</a>
                            <a href ="#">GUIDE</a>
                            <p className="">Hack And love</p>
                            <a href ="#">TELECHARGEMENT</a>
                            <a href ="#">INFORMATIONS</a>
                            <a href ="#">A PROPOS</a>
                        </div>
                        <button className =" navbar-toggler toggler-example black voir " onClick ={() =>this.changeVariable()}><span><i class="fas fa-bars fa-1x"></i></span></button>
                    </div>
                </div>
                <div className={this.state.responsif}>
                        <a href ="#">ACTUALITES</a>
                        <a href ="#">GUIDE</a>
                        <a href ="#">TELECHARGEMENT</a>
                        <a href ="#">INFORMATIONS</a>
                        <a href ="#">A PROPOS</a>
                </div>    
            </div>        
                    
            
        )
    }
}
export default Top;
