import React from 'react';
import druide from './druide.jpeg'
import Sorciere from './diablo-4-sorciere.jpg';
import Lidith from './Lidith.jpg';

class Body extends React.Component{
    constructor(){
        super()
        this.state ={
        image:druide,
        }

    }

    changeSorciere ()  {
        this.setState({
            image: Sorciere,
        })
    }

    changeLidith()   {
        this.setState({
            image:Lidith,
        })
    }

    changeDruide()  {
        this.setState({
            image:druide,
        })
    }


    render(){
        return (
            <div className ="bodyContainer">
                <h1 className="title"> Genèse du jeu</h1>
                <div className="margeTexte"> Hack'n love nacquit de l'idée un peu folle de 4 jeunes passionnés,
                qui voyaient dans le genre hack'n slash une ode a l'amour et la bienveillance.
                Ces quatre petits mousquetaires improvisés pour l'occasion se promirent alors
                de développer le meilleur jeu auquel ils n’auraient jamais jouer.</div>
                <button id="download" className=" btn btn-dark">TELECHARGER LE JEU</button>
                <div className ="containerGallerie">
                    <div className="buttonContainer">
                    <a class="btn-floating btn-sm btn-secondary"><i class="fas fa-star"></i></a>
                        <button onClick={() => this.changeSorciere()}  type="button" className="btn btn-secondary btn-circle btn-sm"></button>
                        <button onClick ={() => this.changeLidith()}  type="button" className="btn btn-secondary btn-circle btn-sm"></button>
                        <button onClick ={()=> this.changeDruide()}  type="button" className="btn btn-secondary btn-circle btn-sm"></button>
                        <button type="button" className="btn  btn-circle btn-sm "></button>
                        <button type="button" className="btn btn-secondary btn-circle btn-sm"></button>
                        <button type="button" className="btn btn-secondary btn-circle btn-sm"></button>

                    </div>
                    <img src ={this.state.image} className ="imgBody" ></img>

                </div>
            
            </div>
        )
    }
}

export default Body;
