    render(){
        return (
            <div>
                <h1 className="title"> Genèse du jeu</h1>
                <div className="margeTexte"> Hack'n love nacquit de l'idée un peu folle de 4 jeunes passionnés,
                qui voyaient dans le genre hack'n slash une ode a l'amour et la bienveillance.
                Ces quatre petits mousquetaires improvisés pour l'occasion se promirent alors
                de développer le meilleur jeu auquel ils n’auraient jamais jouer.</div>
                <button type="button" id ="button" className="btn btn-outline-primary"> Telecharger le jeu </button>
                <div className ="containerGallerie">
                    <div className="buttonContainer">
                        <button onClick={() => this.changeSorciere()} type="button" class="btn btn-secondary btn-circle btn-sm"></button>
                        <button onClick ={() => this.changeLidith()} type="button" class="btn btn-secondary btn-circle btn-sm"></button>
                        <button onClick ={()=> this.changeDuirde()}type="button" class="btn btn-secondary btn-circle btn-sm"></button>
                        <button type="button" class="btn btn-secondary btn-circle btn-sm"></button>
                        <button type="button" class="btn btn-secondary btn-circle btn-sm"></button>
                        <button type="button" class="btn btn-secondary btn-circle btn-sm"></button>

                    </div>
                    <img src ={this.state.image} className ="imgBody1" height="40%" width="40%"></img>

                </div>
            
            </div>
        )
    }
}

export default Body;
